import React, { useRef } from 'react'
import {AiFillCopy} from 'react-icons/ai'

const CenterDetails = ({name,address,pincode,vaccine,date}) =>{
  let pinCodeRef = useRef();
  const copyToClipboard = () =>{
	  var range = document.createRange();
	  range.selectNode(pinCodeRef);
	
	  window.getSelection().addRange(range); // to select text
	//   pinCodeRef.current.style.backgroundColor="yellow";
	//   console.log(pinCodeRef.current.innerHTML.select());
	  document.execCommand("Copy")
	  window.getSelection().removeAllRanges(); // clear current selection
  }
  return(
	<div className="message-box" >
	<h3 className="text-dark">AVAILABLE</h3>
	<div className="center-detail">
	  <div>Center Name = {name}</div>
	  <div>Address = {address}</div>
	  <div>Pin code = 
	  	 <input type="text" className="bg-light p-1" style={{borderRadius:"10px"}} ref={pinCodeRef} value={pincode}readOnly/>
		 <button className="text-success bg-dark mx-2" style={{borderRadius:"10px"}} onClick={copyToClipboard}>Copy <AiFillCopy/></button>
	  </div>
	   
	  <div>Vaccine = {vaccine}</div>
	  <div>Date = {date}</div>
	</div>
  </div>
  )

}

export default CenterDetails
