import axios from "axios";
import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./styles.css";
import AvailabilityDetails from "./Pages/AvailabilityDetails";

import Footer from "./Components/Footer/Footer";
import  ModalWrapper from "./Components/UI/ModalWrapper/ModalWrapper";



const App = () => {

  const [showModal,toggleModal] = useState(false)

  const downloadFAQ = () =>{
    const url="https://firebasestorage.googleapis.com/v0/b/fir-github-api.appspot.com/o/posts%2FFAQ.docx?alt=media&token=42d22d3f-18f9-4481-9f0d-0dace484d4b3"
    try{
      const {data}=axios.get(url,{responseType:"blob"})
      const uri = window.URL.createObjectURL(new Blob([data]))
      const link=document.createElement('a');
      link.href=uri;
      link.setAttribute('download','FAQs');
      link.click()
    }catch(error){
      alert("Something went wrong :(")
    }

  }



  return (
    <>
      <header className="jumbotron ">
        <span className="m-0 p-0"> Vaccine Availability for 18+ in Ahmedabad Corporation </span>
      </header>
      <p className="marquee  text-secondary text-muted p-0">Made By: Mayur keswani</p>
      <button className="faq  float-right faq  btn btn-secondary mr-0" onClick={()=>todownloadFAQggleModal(!showModal)}>FAQ</button>

      <section className="main-section">
      {
        showModal?<ModalWrapper/>:
        <AvailabilityDetails/>
      }
      </section>
      <Footer/>
    </>
  );
};

export default App;
