import axios from "axios";
import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./styles.css";
import CenterDetails from "./Components/Center-Details";
import NoCenter from "./Components/No-Center/NoCenter";
import Footer from "./Components/Footer/Footer";
import  ModalWrapper from "./Components/UI/ModalWrapper/ModalWrapper";



let date = new Date();
let currentDate= date.getDate()
let hourHand=date.getHours();
let month=(date.getMonth())+1
if(hourHand>9){
  ++currentDate;
}

const api =
  `https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/findByDistrict?district_id=770&date=${currentDate}-${month}-2021`;
console.log(currentDate +" - " + month)
const App = () => {
  const [center, setCenter] = useState([]);
  const [showModal,toggleModal] = useState(false)




  return (
    <>
      <header className="jumbotron ">
        <span className="m-0 p-0"> Vaccine Availability for 18+ in Ahmedabad Corporation </span>
      </header>
      <p className="marquee  text-secondary text-muted p-0">Made By: Mayur keswani</p>
      <button className="faq  float-right faq  btn btn-secondary mr-0" onClick={()=>toggleModal(!showModal)}>FAQ</button>

      <section className="main-section">
      {
        showModal?<ModalWrapper/>:

        center.length ? (
        center.map((cntr) => (
          <CenterDetails 
            key={cntr.center_id}
            name={cntr.name} 
            address={cntr.address} 
            pincode={cntr.pincode} 
            vaccine={cntr.vaccine}
            date={cntr.date}
            />

        ))
      ) : (
        <NoCenter/>
      )}
      </section>
      <Footer/>
    </>
  );
};

export default App;
